mapper.update:
UPDATE event
SET name = #{name}, description = #{description}, city = #{city},
    category = #{category}, venue = #{venue}, start_time = #{startTime},
    end_time = #{endTime}, price = #{price}, stock = #{stock},
    status = #{status},ticket_grade = #{ticketGrade}, updated_by = #{updatedBy},updated_time = #{updatedTime}
WHERE id = #{id}

mapper.select:
SELECT * FROM event
WHERE city = #{city}

    AND category IN

        #{category}


ORDER BY created_time DESC

SELECT * FROM event
WHERE 1=1

    AND city = #{city}


    AND category = #{category}

ORDER BY created_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM event
WHERE 1=1

    AND city = #{city}


    AND category = #{category}

SELECT * FROM event
WHERE 1=1

    AND (name LIKE CONCAT('%', #{keyword}, '%')
         OR description LIKE CONCAT('%', #{keyword}, '%'))


    AND city = #{city}


    AND category = #{category}

ORDER BY created_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM event
WHERE 1=1

    AND (name LIKE CONCAT('%', #{keyword}, '%')
         OR description LIKE CONCAT('%', #{keyword}, '%'))


    AND city = #{city}


    AND category = #{category}

mapper.update:
UPDATE event
SET stock = stock - #{quantity}
WHERE id = #{eventId}
  AND stock >= #{quantity}

UPDATE event
SET stock = stock + #{quantity}
WHERE id = #{eventId}
